# math_rumus.py
import math

# Lingkaran
def luas_lingkaran(r):
    return math.pi * r**2

def keliling_lingkaran(r):
    return 2 * math.pi * r

# Segitiga
def luas_segitiga(alas, tinggi):
    return 0.5 * alas * tinggi

# Pythagoras (panjang sisi miring dari siku-siku a dan b)
def pythagoras(a, b):
    return math.hypot(a, b)

# Akar persamaan kuadrat ax^2 + bx + c = 0
def akar_persamaan_kuadrat(a, b, c):
    if a == 0:
        if b == 0:
            return None  # tidak ada solusi atau tak terhingga tergantung c
        return (-c / b, )  # persamaan linear
    D = b*b - 4*a*c
    if D >= 0:
        akar1 = (-b + math.sqrt(D)) / (2*a)
        akar2 = (-b - math.sqrt(D)) / (2*a)
        return akar1, akar2
    # akar kompleks
    real = -b / (2*a)
    imag = math.sqrt(-D) / (2*a)
    return complex(real, imag), complex(real, -imag)

# Faktorial, Permutasi, Kombinasi
def faktorial(n):
    if n < 0:
        raise ValueError("n harus >= 0")
    hasil = 1
    for i in range(2, n+1):
        hasil *= i
    return hasil

def permutasi(n, k):
    if k < 0 or k > n:
        return 0
    hasil = 1
    for i in range(n-k+1, n+1):
        hasil *= i
    return hasil

def kombinasi(n, k):
    if k < 0 or k > n:
        return 0
    k = min(k, n-k)
    atas = 1
    bawah = 1
    for i in range(1, k+1):
        atas *= (n - (i - 1))
        bawah *= i
    return atas // bawah

# Deret
def jumlah_deret_aritmetika(a1, n, d):
    return n/2 * (2*a1 + (n-1)*d)

def jumlah_deret_geometri(a1, n, r):
    if r == 1:
        return a1 * n
    return a1 * (1 - r**n) / (1 - r)

# GCD/LCM
def gcd(a, b):
    a, b = abs(a), abs(b)
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    return abs(a*b) // gcd(a, b) if a and b else 0

# Geometri koordinat
def jarak_2titik(x1, y1, x2, y2):
    return math.hypot(x2 - x1, y2 - y1)

# Konversi sudut
def deg2rad(deg):
    return math.radians(deg)

def rad2deg(rad):
    return math.degrees(rad)

# Contoh penggunaan
if __name__ == "__main__":
    r = 7
    print("Luas lingkaran r=7:", luas_lingkaran(r))
    print("Keliling lingkaran r=7:", keliling_lingkaran(r))
    print("Luas segitiga alas=10, tinggi=6:", luas_segitiga(10, 6))
    print("Pythagoras (3,4):", pythagoras(3, 4))
    print("Akar x^2 - 5x + 6 = 0:", akar_persamaan_kuadrat(1, -5, 6))
    print("Faktorial 5:", faktorial(5))
    print("Permutasi 5P2:", permutasi(5, 2))
    print("Kombinasi 5C2:", kombinasi(5, 2))
    print("Jumlah deret aritmetika a1=2, n=5, d=3:", jumlah_deret_aritmetika(2, 5, 3))
    print("Jumlah deret geometri a1=2, n=5, r=2:", jumlah_deret_geometri(2, 5, 2))
    print("GCD(24,36):", gcd(24, 36))
    print("LCM(24,36):", lcm(24, 36))
    print("Jarak (0,0) ke (6,8):", jarak_2titik(0, 0, 6, 8))
    print("90 derajat ke radian:", deg2rad(90))
    print("pi/2 radian ke derajat:", rad2deg(math.pi / 2))

